mapper.select:
SELECT 
P.CODE,
P.NAME,
R.LIST_PRICE,
R.SALES_PRICE 
FROM PRODUCT P 
LEFT JOIN PRODUCT_PRICE R ON R.PRODUCT_ID = P.ID


		AND P.NAME LIKE #{productName}



		AND R.SALES_PRICE >= #{minPrice}



		AND R.SALES_PRICE <= #{maxPrice}

SELECT P.CODE, P.NAME, R.LIST_PRICE, R.SALES_PRICE, C.NAME FROM PRODUCT P LEFT JOIN PRODUCT_PRICE R ON R.PRODUCT_ID = P.ID LEFT JOIN PRODUCT_CATEGORY PC ON PC.PRODUCT_ID = P.ID LEFT JOIN CATEGORY C ON C.ID = PC.CATEGORY_ID
C.NAME IN
#{category}

mapper.update:
	UPDATE PRODUCT_PRICE


        	LIST_PRICE = #{listPrice},



        	SALES_PRICE = #{salesPrice}


     WHERE ID = #{id}
